import React from 'react';
import { useForm } from "react-hook-form";
import * as Yup from 'yup';
import { yupResolver } from "@hookform/resolvers/yup";
import classes from "./ModalWindow.module.scss";

const schema = Yup.object().shape({
    name: Yup.string().required("Имя обязательно"),
    email: Yup.string().email("Неверный формат email")
        .matches(/^[a-zA-Z0-9._%+-]+@gmail\.com$/, "Email должен заканчиваться на @gmail.com")
        .required("Email обязателен"),
    password: Yup.string().required("Пароль обязателен"),
    password2: Yup.string()
        .oneOf([Yup.ref('password'), null], "Пароли должны совпадать")
        .required("Подтверждение пароля обязательно"),
});

const ModalWindow = () => {
    const {
        register,
        handleSubmit,
        formState: { errors }
    } = useForm({
        resolver: yupResolver(schema)
    });

    const submit = (data) => {
        console.log(data);
    };

    return (
        <div className={classes.modal}>
            <h1>Зарегистрируйтесь через</h1>
            <form onSubmit={handleSubmit(submit)}>
                <div className={classes.modalWindow}>
                    <div className={classes.div}>
                        <h2>Имя</h2>
                        <input
                            type="text"
                            placeholder="Ваше полное имя"
                            className={classes.input}
                            {...register("name")}
                        />
                        {errors.name && <p className={classes.error}>{errors.name.message}</p>}
                    </div>
                    <div className={classes.div}>
                        <h2>Email</h2>
                        <input
                            type="text"
                            placeholder="Ваш email адрес"
                            className={classes.input}
                            {...register("email")}
                        />
                        {errors.email && <p className={classes.error}>{errors.email.message}</p>}
                    </div>
                    <div className={classes.div}>
                        <h2>Пароль</h2>
                        <input
                            type="password"
                            placeholder="Ваш пароль"
                            className={classes.input}
                            {...register("password")}
                        />
                        {errors.password && <p className={classes.error}>{errors.password.message}</p>}
                    </div>
                    <div className={classes.div}>
                        <h2>Подтверждение пароля</h2>
                        <input
                            type="password"
                            placeholder="Подтвердите пароль"
                            className={classes.input}
                            {...register("password2")}
                        />
                        {errors.password2 && <p className={classes.error}>{errors.password2.message}</p>}
                    </div>
                    <button type="submit">ПРОДОЛЖИТЬ</button>
                </div>
            </form>
        </div>
    );
};

export default ModalWindow;
